//            if (A[i]==x){
//                return i;
//            }
//        }
//        return -1;
//    }

    public int findElement(int[] A, int n, int x) {
        // write code here
        int i=0,j=n-1,mid;
        while(i<=j) {
            mid=(i+j)/2;
            if(A[mid]==x) {
                return mid;
            }
            if(A[mid]<x) {
                //A[mid]<A[left] 说明右边是有序的，且x>A[right]说明x在mid左边
                if(A[mid]<A[i]&&x>A[j]) {
                    j=mid-1;
                } else {
                    i=mid+1;
                }
            }
            else {
                //A[mid]>A[left]说明左边是有序的，且x<A[left]，说明x在mid右边
                if(A[mid]>A[i]&&x<A[i]) {
                    i=mid+1;
                } else {
                    j=mid-1;
                }
            }
        }
        return -1;
    }



