package com.nuc.zp.leetcode.item101_200;

import java.util.Arrays;
import java.util.HashMap;
import java.util.Map;
import java.util.stream.Collectors;

/**
 * 给定一个字符串，编写一个函数判定其是否为某个回文串的排列之一。
 * <p>
 * 回文串是指正反两个方向都一样的单词或短语。排列是指字母的重新排列。
 * <p>
 * 回文串不一定是字典当中的单词。
 * <p>
 *  
 * <p>
 * 示例1：
 * <p>
 * 输入："tactcoa"
 * 输出：true（排列有"tacocat"、"atcocta"，等等）
 * <p>
 * 来源：力扣（LeetCode）
 * 链接：https://leetcode-cn.com/problems/palindrome-permutation-lcci
 * 著作权归领扣网络所有。商业转载请联系官方授权，非商业转载请注明出处。
 */
public class CanPermutePalindrome0104 {
    Map<String, Integer> map = new HashMap<>();

    public boolean canPermutePalindrome(String s) {
        String[] splits = s.split("");
        for (String str : splits) {
            Integer val = map.get(str);
            if (val == null) {
                map.put(str, 1);
            } else {
                val += 1;
                map.put(str, val);
            }
        }
        System.out.println(map.values());
        return map.values().stream().filter(val -> (val & 1) == 1).count() <= 1;

    }

    public static void main(String[] args) {
        CanPermutePalindrome0104 canPermutePalindrome0104 = new CanPermutePalindrome0104();
        System.out.println(canPermutePalindrome0104.canPermutePalindrome(""));
    }
}
